mapper.select:
		select

		from game_redeem_code where game_id=#{gameId} and act_id=#{actId}


			and player_id = #{playerId}


 		and score = #{searchScore} and update_time  <=  #{eTime} and update_time >= #{bTime} and delete_time  <=  0 and status = #{status}
		ORDER BY update_time desc limit #{startRow},#{endRow};

		select

		from game_redeem_code where game_id=#{gameId} and act_id=#{actId}

 		and score = #{score} and update_time  <=  #{eTime} and update_time >= #{bTime} and status = #{status}
		ORDER BY update_time desc;

select distinct score from game_redeem_code where game_id=#{gameId} and  act_id=#{actId} 


	and player_id = #{playerId}


and delete_time  <=  0 order by score+0 asc;
