mapper.select:
		select

		from tb_exchange
         where gameName = #{gameName} or 'SUPER_ADMIN'=#{userRole}
		ORDER BY id desc limit #{startRow},#{endRow};

select 'false' from tb_exchange where  gameName=#{gameName} and (
             ( exchEndTime > #{exchBeginTime} and exchEndTime < #{exchEndTime} ) or 
             ( exchBeginTime < #{exchBeginTime} and exchEndTime > #{exchEndTime}) or
             ( exchBeginTime > #{exchBeginTime} and exchEndTime > #{exchEndTime} and exchBeginTime <#{exchEndTime})
             ) order by exchEndTime desc limit 1;

select 'false' from tb_exchange where  gameName=#{gameName} and (
             ( exchEndTime > #{exchBeginTime} and exchEndTime < #{exchEndTime} ) or 
             ( exchBeginTime < #{exchBeginTime} and exchEndTime > #{exchEndTime}) or
             ( exchBeginTime > #{exchBeginTime} and exchEndTime > #{exchEndTime} and exchBeginTime <#{exchEndTime})
             ) and id <> #{id} order by exchEndTime desc limit 1;

select

from tb_exchange
where gameName= #{gameName} and  exchBeginTime >= #{exchBeginTime} and exchEndTime <= #{exchEndTime} limit #{startRow},#{endRow};

select

from tb_exchange
where gameName= #{gameName} and exchBeginTime <= #{currentDate} and exchEndTime >= #{currentDate} order by exchEndTime desc,id desc limit 1;
